public class ExFlow_3_2
{
	// 상수 
	// 멤버변수
	// 생성자

	// 함수 

	// main() 함수 : 프로그램 시작점
	public static void main(String[] args) {
		// TODO Auto-generated method stub.
		
		int isud = 'I';


		if ('I' == isud)
		{
			System.out.println("인서트 하기.");
		}
		if ('S' == isud)
		{
			System.out.println("셀렉트 하기.");
		}
		if ('U' == isud)
		{
			System.out.println("업데이트 하기.");
		}
		if ('D' == isud)
		{
			System.out.println("딜리트 하기.");
		}

		// 16진수로 비교 해보기 
		if (0x49 == isud)
		{
			System.out.println("인서트 하기.");
		}
		if (0x53 == isud)
		{
			System.out.println("셀렉트 하기.");
		}
		if (0x55 == isud)
		{
			System.out.println("업데이트 하기.");
		}
		if (0x44 == isud)
		{
			System.out.println("딜리트 하기.");
		}
	}
}
